

puzzle_input = ''


def test_packet(testdata):
    for letter in testdata:
        if testdata.count(letter) > 1:
            return False
    return True


packet_location = 0
packet_marker = ''
for x in range(len(puzzle_input)-4):
    current_packet = puzzle_input[x:x+4]
    if test_packet(current_packet):
        packet_location = x + 4
        packet_marker = current_packet
        break

print(f'{packet_location}:{packet_marker}')