

puzzle_input = ''
# puzzle_input = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'

def test_packet(testdata):
    for letter in testdata:
        if testdata.count(letter) > 1:
            return False
    return True


packet_location = 0
packet_marker = ''
packet_size = 14
for x in range(len(puzzle_input)-packet_size):
    current_packet = puzzle_input[x:x+packet_size]
    if test_packet(current_packet):
        packet_location = x + packet_size
        packet_marker = current_packet
        break

print(f'{packet_location}:{packet_marker}')